codeCss:
h1 {
  color: hsl(0 0% 15%);
  font-family: serif;
  font-weight: 500;
}
p {
  color: hsl(0 0% 5%);
  font-family: sans-serif;
  font-weight: 400;
}

.rood { color: red; }
        .groen { color: green; }
        .blauw { color: blue; }

img {
        inline-size: 100%;
        max-inline-size: 400px;
        block-size: auto;
      }
      figure {
        margin: 0;
      }
      figcaption {
        margin-block-end: 1em
      }

a {
        display: flex;
        column-gap: 1em;
      }
      img {
        block-size: 120px;
        inline-size: auto;
      }

.clickable {
  border-radius: 0.25rem;
  border: 1px solid hsl(0 0% 0% / 0.2);
  max-width: 60ch;
  padding: 0.5rem;
  position: relative;
  transition: box-shadow 150ms ease-out;
}
.clickable:hover,
.clickable:focus-within {
  box-shadow: 1px 1px 4px 2px hsl(0 0% 0% / 0.2);
}
.clickable a::after {
  content: "";
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

a:any-link {
  text-decoration: none;
  color: inherit;
}
